import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import {useState} from "react";
import {useEffect} from "react";

import Grid from "@material-ui/core/Grid"
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";

function App() {
  return (
    <div className="app">
      <SearchBar />
      <MovieList />
    </div>
  );
}

function SearchBar() {
  return (
    <div id="search">
      <h1>Movies</h1>
      <input
        id="searchBar"
        type="text"
        placeholder="Search Movies"
      />
      <button className="searchBtn">Find Movie</button>
    </div>
    );
}

// list of movies from search results
function MovieList() {
  const [movies, setMovies] = useState(null);

    const movieTitle = "aladdin";

    // search movies
    useEffect(()=> {
        fetch(`http://www.omdbapi.com/?s=${movieTitle}&apikey=52514a3a`)
          .then(response => response.json())
          .then(setMovies)
          .catch(console.error);
    }, []);

    // display search results
    if (movies && movies.Response === "True") {
      // display a card for every movie in search results list
      return (
           <div className="movieList">
             {movies.Search.map(movie => {
                return <MovieCard name={ movie.Title}
                              releaseDate={movie.Year} runtime="1 hr" genre="family" director="director"/>
              })}
           </div>
         );
    }

  return (
    <div className="movieList">
      <h2>No results</h2>
    </div>
  );
}

// card showing details of a specific movie
function MovieCard({name, releaseDate, runtime, genre, director}) {
  const [movieData, setMovieData] = useState(null);

  var runtime = "Unknown duration";
  var genre = "Unknown genre";
  var director = "Unknown director";

  // find details of movie
  useEffect(()=> {
      fetch(`http://www.omdbapi.com/?t=${name}&apikey=52514a3a`)
        .then(response => response.json())
        .then(setMovieData)
        .catch(console.error);
  }, []);

  if (movieData && movieData.Response === "True") {
    runtime = `${movieData.Runtime}`;
    genre = `${movieData.Genre}`;
    if (movieData.Director !== "N/A")
      director = `${movieData.Director}`;
  }

  return (
    <div >
      <Card  style={{backgroundColor: 'transparent', margin: "20px", borderRadius: "20px"}}>
        <CardActionArea >
          <Grid container
            className="cardGridContainer"
            direction = "column"
            justify = "center"
            spacing = {2} >

          {/*movie title*/}
            <Grid item style={{paddingTop: "25px", marginBottom: "20px"}} className="movieTitle">{name}</Grid>

          {/* horizontal row of movie details */}
            <Grid container
              className = "cardDetailsContainer"
              direction='row'
              justify = "center"
              spacing = {7}>
              <Grid item>{releaseDate}</Grid>
              <Grid item>{runtime}</Grid>
              <Grid item>{genre}</Grid>
              <Grid item>{director}</Grid>
            </Grid>

          </Grid>
        </CardActionArea>
      </Card>
    </div>
  );
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
);
